  return <main>

    <header> 
      <img src="http://assets.stickpng.com/thumbs/580b57fcd9996e24bc43c541.png" 
      className="logo"
      />
      Fresh memes
    </header>

    {/* 
      You can comment like this.
      A comment requires curly braces because we are in
      HTML??? or JSS. I have no idea. but it works!!!
    */}

    <TextInput onSend={t=> console.log(t)} />

  </main>
}

function TextInput(props){
  const [text, setText] = useState('')

  return <div className="text-input">
    <input value={text}
      placeholder="Enter text"
      onChange={e=> setText(e.target.value)}
      style={{"position":"absolute",
              "left":"15px",
              "width":"270px",
              "border-radius":"15px",
              "border":"1px solid white",
              "background":"black",
              "color":"white",
              "height":"24px",
              "align":"center"}}
    />
    <button onClick={()=> {
      props.onSend(text)
      setText('')
      }}
      style={{
        "position":"absolute",
        "top":"12px",
        "right":"28px",
        "width":"20px",
        "height":"20px",
        "border-radius":"15px",
        "border":"1px solid white",
        "display":"flex",
        "padding-right":"10px"
        }}
    >
      â†‘
    </button>
  </div>
}

export default App;
